import React from "react";
import local from "../data/local";

export const initialState = {
    proxy: "http://localhost:5000",
    testId: "0",
    testTitle: "Jee Advance",

    sectionDetails: local.sectionDetails,

    topicId: "5f5b576203130afeafb583d3",

    topicDetails: {"_id":"5f5b576203130afeafb583d3","name":"Relations and Functions","questions":[{"options":["5","-5","0","2"],"_id":"5f5b576203130afeafb583d4","text":"Which is greater than 4?","answer":{"_id":"5f5b576203130afeafb583d5","index":1,"value":"5"}},{"options":["Electric field","Magnetic Field","emf","force"],"_id":"5f5b71a7301aa712b9bb779f","text":"What is the full form of emf?","answer":{"_id":"5f5b71a7301aa712b9bb77a0","index":1,"value":"5"}}],"__v":1},
    visitedQuestion: [true, false],
    activeQuestion: 1,
    answeredQuestion: [false, false],
    questionTimer: ["1233", "10"],
    yourAnswer: [1, 4],
    yourScore: 5,

    resultClass: ["resultSuccess", "resultFailure"],

    users:[{"_id":"5f5fa0a94d03c4792875b6a4","firstname":"Rohit","lastname":"Singh","email":"rohitsingh18997@gmail.com","password":"1234","testAttempted":[],"createdAt":"2020-09-14T16:56:09.369Z","updatedAt":"2020-09-14T16:56:09.369Z","__v":0}],

    userId:"1",
    userDetails: {"name": "Rohit", "testAttempted":[{"name": "Advance", "scoredMarks":"12", "totalMarks":"20"},{"name": "Cat", "scoredMarks":"5", "totalMarks":"10"}]}

};

let reducer = (state, action) => {

    switch (action.type){

        case 'UPDATE_TEST_ID':
            return {
                ...state,
                testId: action.item
            }

        case 'UPDATE_TEST_TITLE':
            return {
                ...state,
                testTitle: action.item
            }

        case 'UPDATE_TOPIC_ID':
            return {
                ...state,
                topicId: action.item
            }

        case 'UPDATE_TOPIC_DETAIL':
            return {
                ...state,
                topicDetails: action.item
            }

        case 'SET_ACTIVE_QUESTION':
            return {
                ...state,
                activeQuestion: action.item
            }
        case 'SET_VISITED_QUESTION':

            return {
                ...state,
                visitedQuestion: action.item
            }

        case 'UPDATE_VISITED_QUESTION':
            var list= state.visitedQuestion;
            list[parseInt(action.item)] = true;

            return {
                ...state,
                visitedQuestion:list
            }

        case 'SET_ANSWERED_QUESTION':
            return {
                ...state,
                answeredQuestion: action.item
            }

        case 'UPDATE_ANSWERED_QUESTION':
            var list = state.answeredQuestion;
            list[state.activeQuestion-1] = action.item

            return {
                ...state,
                answeredQuestion: list
            }

        case 'SET_YOUR_ANSWER':
            return {
                ...state,
                yourAnswer: action.item
            }

        case 'UPDATE_YOUR_ANSWER':
            var list = state.yourAnswer;
            list[state.activeQuestion-1] = action.item

            return {
                ...state,
                yourAnswer: list
            }

        case 'UPDATE_RESULT_CLASS':
            return {
                ...state,
                resultClass: action.item
            }

        case 'SET_YOUR_SCORE':
            return {
                ...state,
                yourScore: action.item
            }

            ////

        case 'SET_QUESTION_TIMER':
            return {
                ...state,
                questionTimer: action.item
            }



        case 'UPDATE_QUESTION_TIMER':
            list = state.questionTimer;
            list[state.activeQuestion-1] = action.item;

            return {
                ...state,
                questionTimer: list
            }




        case 'SET_USERS':
            return {
                ...state,
                users: action.item
            }
        case 'ADD_USER':
            return {
                ...state,
                users: [...state.users, action.item]
            }
        case 'SET_USER_ID':
            return {
                ...state,
                userId: action.item
            }

        default:
            return state;
    }
}

export default reducer;
