import React, {useState} from "react";
import Subject from "./Subject";
import "./Section.css";
import {useStateValue} from "./StateProvider";


function Section(){
    const [state, dispatch] = useStateValue();

    fetch(`localhost:6000/tests/${state.testId}`)
        .then(res => res.json())
        .then(data => {

        });

    const [chapter, setChapter] = useState([
        {
            subject: "Physics",
            chapters: ["Electrostatics",
                "Mechanical Dynamics", "Wave Motion"],
            open:true
        },
        {
            subject: "Mathematics",
            chapters: ["Relation and Functions",
                "Trignometry", "Differential Equations"],
            open:false
        },
        {
            subject: "Chemistry",
            chapters: ["Mole Concept",
                "Solid, Liquid and Gas", "Organic Chemistry"],
            open:false
        }
    ]);

    const toogleSubject = function (index){
        setChapter(chapter.map((info, i) =>{
            if(i == index){
                info.open = !info.open
            }
            else{
                info.open = false;
            }
            return info;
        }))
    };

    return(
        <div className={"section"}>
            <h1>Jee Advance Mock Test</h1>
            <div className={"subsection"}>
                {chapter.map((info, i) => {
                    return(
                        <div>
                            <Subject info={info}
                                     index={i}
                                     toggleSubject = {toogleSubject}
                            />
                        </div>
                    )
                })}
            </div>
        </div>
    );
}
export default Section;
