import React, {useEffect, useState} from "react";
import "./Quiz.css";
import TimerHeader from "../components/TimerHeader";
import {useStateValue} from "../contextAPI/StateProvider";
import QuestionPanel from "../components/QuestionPanel";
import SidePanel from "../components/SidePanel";

function Quiz(){
    const [state, dispatch] = useStateValue();

    const [innerWidth, setInnerWidth] = useState(1500);

    const setTopicDetails =  () => {
        fetch(`http://localhost:5000/topics/${state.topicId}`)
            .then(res => res.json())
            .then(data=>{
                console.log(data);
                dispatch({
                    type: 'UPDATE_TOPIC_DETAILS',
                    item: data
                })
            })
            .catch(err => {
                console.log(err)
            });
    }

    useEffect(() => {

        setTopicDetails();

        return ()=>{};
    }, []);
    useEffect(()=>{

        setInnerWidth(window.innerWidth);

        let numberOfQuestions = state.topicDetails.questions.length;
        console.log(numberOfQuestions);

        if(numberOfQuestions> 0){

            let list = Array(numberOfQuestions).fill(false);
            list[0] = true;
            dispatch({
                type: 'SET_VISITED_QUESTIONS',
                item: list
            });

            dispatch({
                type: 'SET_ACTIVE_QUESTION',
                item: 1
            });

            list = Array(numberOfQuestions).fill(false);
            dispatch({
                type: 'SET_ANSWERED_QUESTIONS',
                item: list
            });

            list = Array(numberOfQuestions).fill(0);
            dispatch({
                type: 'SET_YOUR_ANSWER',
                item: list
            });
        }
        console.log(state.topicDetails);

        return ()=>{};
    }, [state.topicDetails]);


    function openNav() {
        document.getElementById("sideBar").style.width = "250px";
        document.getElementById("collapseButton").style.marginLeft = "250px";
    }

    /* Set the width of the sidebar to 0 and the left margin of the page content to 0 */
    function closeNav() {
        document.getElementById("sideBar").style.width = "0";
        document.getElementById("collapseButton").style.marginLeft = "0";
    }

    return(
        <div className={"quiz"}>
            <TimerHeader/>
            <div className={"quiz__content"}>
                <div className={"quiz__content__leftSide"}>
                    <QuestionPanel/>
                </div>
                {innerWidth < 720? (
                    <div className={"quiz__content__rightSide"}>
                        <div id={"sideBar"}>

                            <SidePanel/>
                        </div>
                        <div id={"collapseButton"}>
                            <button onClick={openNav}>&#9776;</button>
                        </div>

                    </div>
                ):(
                    <div className={"quiz__content__rightSide"}>
                        <SidePanel/>
                    </div>
                )}

            </div>
        </div>
    );
}

export default Quiz;
