import React, {useState} from "react";
import "./Subject.css";
import {useHistory} from "react-router-dom";
import {useStateValue} from "../contextAPI/StateProvider";

function Subject(props){
    const [state, dispatch] = useStateValue();

    const history = useHistory();

    const createAlert = (index, topic_id) =>{
        // eslint-disable-next-line no-restricted-globals
        fetch(`http://localhost:5000/tests/${state.testId}/stream/${state.testStreamId}/topic/${topic_id}`)
            .then((res)=>res.json())
            .then((data)=>{
                console.log(data);
                dispatch({
                    type: 'UPDATE_TOPIC_DETAILS',
                    item: data
                })
            });
        // eslint-disable-next-line no-restricted-globals
        var flag = confirm("Are you sure you want to do Quiz?");
        if(flag){

            history.push('/quiz');
        }
    }

    return(
        <div className={"subject "+ (state.testStreamsInfo[props.index].open ? "open": "")}
            key={props.index}
             onClick={()=>props.toggleSubject(props.index, state.testStreamsInfo[props.index]._id)}
        >
            <div className={"subject__heading"}>
                {state.testStreamsInfo[props.index].name}
            </div>
            <div className={"subject__topics"}>
                {
                    state.testStreamsInfo[props.index].topic.map((chapter, i) =>{
                        return(
                            <div onClick={()=>createAlert(i, chapter._id)}>
                                {chapter.name}
                            </div>

                        );
                    })
                }
            </div>
        </div>
    );
}
export default Subject;
