import React from "react";

export const initialState = {
    testId: "0",
    testTitle: "Jee Advance",
    sectionDetails: [
        [
            {
                name:"Physics",
                topics:[
                    {name: "Electrostatics", id: "5f5b6d99e329b50bfd0329f5"},
                    {name: "Simple Harmonic Motion", id:"5f5b6d99e329b50bfd0329f5"},
                    {name:"Friction", id:"5f5b6d99e329b50bfd0329f5"}
                ]
            },
            {
                name:"Chemistry",
                topics:[
                    {name: "Mole Concept", id: "5f5b6d99e329b50bfd0329f5"},
                    {name: "Solid, Liquid and Gas", id:"5f5b6d99e329b50bfd0329f5"},
                    {name: "Organic", id:"5f5b6d99e329b50bfd0329f5"}
                ]
            },
            {
                name:"Mathematics",
                topics:[
                    {name: "Relations and Functions", id: "5f5b576203130afeafb583d3"},
                    {name: "Trignometric Equations", id:"5f5b6d99e329b50bfd0329f5"},
                    {name: "Mathematical Induction", id:"5f5b6d99e329b50bfd0329f5"}
                ]
            }
        ],
        [
            {
                name:"Database Management System",
                topics:[
                    {name: "Normalization", id: "5f5b6d99e329b50bfd0329f5"},
                    {name: "Transaction", id:"5f5b6d99e329b50bfd0329f5"},
                ]
            },
            {
                name:"Operating System",
                topics:[
                    {name: "Process Management", id: "5f5b6d99e329b50bfd0329f5"},
                    {name: "Memory Management", id:"5f5b6d99e329b50bfd0329f5"},
                ]
            },
        ],
        [
            {
                name:"Aptitude Ability",
                topics:[
                    {name: "Logical Reasoning", id: ""},
                    {name: "Probability", id:""},
                ]
            }
        ]
    ],
    topicId: null,

    topicDetails: {"_id":"5f5b576203130afeafb583d3","name":"Relations and Functions","questions":[{"options":["5","-5","0","2"],"_id":"5f5b576203130afeafb583d4","text":"Which is greater than 4?","answer":{"_id":"5f5b576203130afeafb583d5","index":1,"value":"5"}},{"options":["5","-5","0","2"],"_id":"5f5b71a7301aa712b9bb779f","text":"What is the full form of emf?","answer":{"_id":"5f5b71a7301aa712b9bb77a0","index":1,"value":"5"}}],"__v":1},
    result: ["", ""],

    user:null,
    userRecord:[{
        testTitle:"Advanced Physics",
        totalMarks: 10,
        scoredMarks: 15
    }],

};

let reducer = (state, action) => {

    switch (action.type){
        case 'UPDATE_TEST_ID':
            return {
                ...state,
                testId: action.item
            }

        case 'UPDATE_TEST_TITLE':
            return {
                ...state,
                testTitle: action.item
            }

        case 'UPDATE_TOPIC_DETAILS':
            return {
                ...state,
                topicDetails: action.item
            }

        case 'UPDATE_TOPIC_ID':
            return {
                ...state,
                topicId: action.item
            }

        case 'UPDATE_RESULT_CLASS':
            return {
                ...state,
                result: action.item
            }

        case 'UPDATE_USER_ID':
            return {
                ...state,
                userId: action.item
            }

        case 'UPDATE_USER_RECORD':
            return {
                ...state,
                userRecord:[...state.userRecord, action.item]
            }

        default:
            return state;
    }
}

export default reducer;
