import React, {useEffect, useState} from "react";
import "./TimerHeader.css";
import Button from "@material-ui/core/Button";
import {useHistory} from "react-router-dom";

function TimerHeader({quiz}){
    const [hour, setHour] = useState(["00"]);
    const [minute, setMinute] = useState(["15"]);
    const [second, setSecond] = useState(["14"]);

    const history = useHistory();

    const resultCalculation = function (){
        var answers = [], predictedAnswer = [];
        quiz.map((info, i)=>{
            answers.push(info.answer.index);
        });

        var elements = document.getElementsByClassName("questionCard__question__options");
        elements.map((ele, i)=> {

        })


        alert("Your test is over. Please click on okay to see the result");

        setTimeout(()=> {
            history.push("/result");
        }, 1000);
    };

    useEffect(() => {
        (function (){
            setTimeout(function (){
                if(second == "00"){
                    if(minute =="00"){
                        resultCalculation();
                    }
                    else {
                        var min = parseInt(minute) - 1;
                        if (min < 10)
                            min = "0" + min.toString();
                        setMinute(min);
                        setSecond("59");
                    }
                }
                else if(second != "00"){
                    var sec = parseInt(second)-1;
                    if(sec < 10)
                        sec = "0"+sec.toString()
                    setSecond(sec);
                }
            }, 1000);
        })();

        return ()=>{};

    }, [second]);

    return(
        <div className={"timerHeader"}>
            <div className={"timerHeader__title"}>
                <p>Electrostatics</p>
            </div>
            <div className={"timerHeader__clock"}>
                <p>Time Left </p>

                <div className={"timerHeader__clock__container"}>
                    <div className={"timerHeader__clock__time"}>
                        {hour}
                    </div>
                    <p>:</p>
                    <div className={"timerHeader__clock__time"}>
                        {minute}
                    </div>
                    <p>:</p>
                    <div className={"timerHeader__clock__time"}>
                        {second}
                    </div>
                </div>
            </div>
            <div className={"timerHeader__button"}>
                <Button variant="contained" color="primary">
                    End Test
                </Button>
            </div>
        </div>
    );
}

export default TimerHeader;
