import React, {useEffect, useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";
import "./QuestionPanel.css";

function QuestionPanel(){
    const [state, dispatch] = useStateValue();


    const [minute, setMinute] = useState([parseInt(state.questionTimer)/60].toString());
    const [second, setSecond] = useState([parseInt(state.questionTimer)%60].toString());

    useEffect(() => {
        (function (){
            setTimeout(function (){
                if(minute == "0")
                    setMinute("00")

                if(second == "59"){
                    let min = parseInt(minute)+1;
                    if (min < 10)
                        min = "0" + min.toString();
                    setMinute(min);
                    setSecond("00");
                }
                else if(second != "60"){
                    let sec = parseInt(second)+1;
                    if(sec < 10)
                        sec = "0"+sec.toString()
                    setSecond(sec);
                }
            }, 1000);
        })();

        return ()=>{};
    }, [second]);

    return(
        <div className={"questionPanel"}>
            <div className={"questionPanel__header"}>
                <strong className={"questionPanel__header__title"}>
                    Question No. {state.activeQuestion}
                </strong>
                <div className={"questionPanel__header__marking"}>
                    <p>Marks</p>
                    <div className={"questionPanel__header__marking__digit"}>
                        <a style={{background: "green"}}>+4</a>
                        <a  style={{background: "red"}}>-1</a>
                    </div>
                </div>
                <div className={"questionPanel__header__clock"}>
                    <p>Time</p>
                    <div className={"questionPanel__header__clock__time"}>
                        <p>{minute}</p>
                        <a>:</a>
                        <p>{second}</p>
                    </div>
                </div>

            </div>

            <hr/>

            <div className={"questionPanel__question"}>
                <div className={"questionPanel__question__text"}>
                    <p>{state.topicDetails.questions[state.activeQuestion-1].text}</p>
                </div>
                <div className={"questionCard__question__options"}>
                    {
                        state.topicDetails.questions[state.activeQuestion-1].options.map((info, i)=>{
