import React from "react";

export const initialState = {
    quiz: [
            {
                question: "Superposition principle can not be applied to these quantities in electrostatics.",
                options: ["Electrostatic energy", "Electric Field", "Electric Potential", "Electric force"],
                answer: {
                    index: 1,
                    value: "Electrostatic energy"
                },
                result:{
                    status: false,
                    class: ""
                }
            },
            {
                question: "Two conducting charged spheres X and Y having unequal charges are connected by the " +
                    "wire. Which of the following is true?",
                options: ["Electrostatic energy is conserved", "Charge is conserved",
                    "Both the charge and electrostatic energy is conserved", "Neither of these is conserved"],
                answer: {
                    index: 2,
                    value: "Charge is conserved"
                },
                result:{
                    status: false,
                    class: ""
                }
            }
        ],
    testId: null,
    testTitle: null,
    testStreamsInfo: null,
    testStreamId: null,

    topicDetails: null,
    result: null

};

function reducer(state, action){

    switch (action.type){
        case 'UPDATE_QUIZ_INFO':
            //Logic for adding item to basket
            return {
                ...state,
                quiz: action.item
            }
        case 'UPDATE_TEST_ID':
            //Logic for updating test id
            return {
                ...state,
                testId: action.item
            }
        case 'UPDATE_TEST_TITLE':
            //Logic for updating test title
            return {
                ...state,
                testTitle: action.item
            }
        case 'UPDATE_TEST_STREAMS_INFO':
            //Logic for updating test streams
            return {
                ...state,
                testStreamsInfo: action.item
            }
        case 'UPDATE_TEST_STREAM_ID':
            //Logic for updating test title
            return {
                ...state,
                testStreamId: action.item
            }
        case 'UPDATE_TOPIC_DETAILS':
            //Logic for updating test title
            return {
                ...state,
                topicDetails: action.item
            }
        case 'UPDATE_RESULT':
            //Logic for updating test title
            return {
                ...state,
                result: action.item
            }
        default:
            return state;
    }
}

export default reducer;
