import React, {useEffect} from 'react';
import './App.css';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Section from "./pages/Section";
import Quiz from "./pages/Quiz";
import Result from "./pages/Result";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import {useStateValue} from "./contextAPI/StateProvider";
import Logs from "./pages/Logs";
import {defaultState} from "./data/defaultState";

function App() {
    const [state, dispatch] = useStateValue();

    function saveToLocalStorage(){
        try{
            const serializedState = JSON.stringify(state);
            localStorage.setItem('state', serializedState);
        }catch (err){
            console.log(err);
        }
    }

    function loadDefaultState(){
        try{
            const serializedState = localStorage.getItem('state');
            if(serializedState == null){
                return undefined
            }
            return JSON.parse(serializedState);
        }catch (err){
            console.log("Debug: "+ err);
            return undefined
        }
    }

    useEffect(()=>{
        const serializedState = loadDefaultState();

        if(!serializedState){
            const serializedDefaultState = JSON.stringify(defaultState);
            localStorage.setItem('state', serializedDefaultState);
        }
        let a = defaultState;

        console.log("DEBUG:" + a);

    }, []);

    useEffect(()=>{

        if(state!= undefined)
            saveToLocalStorage();

    }, [state]);


    useEffect(() =>{
        fetch('http://localhost:5000/users')
            .then(res => res.json())
            .then(users =>  {
                console.log(users);

                dispatch({
                    type: "SET_USERS",
                    item: users
                });
            });
        fetch('http://localhost:5000/user/getUser')
            .then(res => res.json())
            .then(user =>  {
                console.log(user);

                if(user){
                    dispatch({
                        type: "SET_USER_ID",
                        item: user._id
                    });

                    let usersDetails={
                        "firstname": user.firstname,
                        "lastname": user.lastname,
                        "email": user.email,
                        "testAttempted": user.testAttempted
                    };

                    dispatch({
                        type: "SET_USER_DETAILS",
                        item: usersDetails
                    });
                }

            })
    },[]);

  return (
    <div className="app">
      <Router>
        <div className="app">
          <Switch>

              <Route path={"/quiz"}>
                  <Header/>
                  <Quiz/>
              </Route>

              <Route path={"/login"}>
                  <Header/>
                  <Login/>
                  <Footer/>
              </Route>

              <Route path={"/signup"}>
                  <Header/>
                  <Signup/>
                  <Footer/>
              </Route>

              <Route path={"/result"}>
                  <Header/>
                  <Result/>
                  <Footer/>
              </Route>

              <Route path={"/section"}>
                  <Header/>
                  <Section/>
                  <Footer/>
              </Route>

              <Route path={"/yourLogs"}>
                  <Header/>
                  <Logs/>
                  <Footer/>
              </Route>

                <Route path="/">
                    <Header/>
                    <Home/>
                    <Footer/>
                </Route>



          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
