import React, {useEffect} from "react";
import "./Quiz.css";
import TimerHeader from "./TimerHeader";
import QuestionCard from "./QuestionCard";
import {useStateValue} from "./StateProvider";

function Quiz(){
    const [state, dispatch] = useStateValue();
    useEffect(()=>{
        let numberOfQuestions = state.questions.length;
        var result = [];
        for(var i=0; i<numberOfQuestions; i++){
            result.push({
                status: false,
                class: ""
            });
        }
        dispatch({
            type: 'UPDATE_RESULT',
            item: result
        })

        return ()=>{};
    })
    return(
        <div className={"quiz"}>
            <TimerHeader />
            <div className={"quiz__questionSet"}>
                {
                    state.questions.map((info, i)=>{
                    return(
                        <div className={"quiz__questionSet__question"}>
                            <QuestionCard question={info.text}
                                          answer={info.answer}
                                          options={info.options}
                                          index={i}
                                          result={(state.result)[i]}
                            />
                        </div>

                        )

                })}
            </div>

        </div>
    );
}

export default Quiz;
