import React, {useState} from "react";
import "./Subject.css";
import {useHistory} from "react-router-dom";
import {useStateValue} from "../contextAPI/StateProvider";

function SectionCard({sectionId, toggleSection, openClass, sectionName}){
    const [state, dispatch] = useStateValue();

    const topic = []


    const history = useHistory();


    const createConfirm = (index, topic_id) =>{
        // eslint-disable-next-line no-restricted-globals
        fetch(`http://localhost:5000/tests/${state.testId}/stream/${state.testStreamId}/topic/${topic_id}`)
            .then((res)=>res.json())
            .then((data)=>{
                console.log(data);
                dispatch({
                    type: 'UPDATE_TOPIC_DETAILS',
                    item: data
                })
            });
        // eslint-disable-next-line no-restricted-globals
        var flag = confirm("Are you sure you want to do Quiz?");
        if(flag){

            history.push('/quiz');
        }
    }

    return(
        <div className={"subject "+ (openClass[sectionId]? "open": "")}
            key={sectionId}
             onClick={()=>toggleSection(sectionId)}
        >
            <div className={"subject__heading"}>
                {sectionName}
            </div>
            <div className={"subject__topics"}>
                {

                    state.testStreamsInfo[props.index].topic.map((chapter, i) =>{
                        return(
                            <div onClick={()=>createConfirm(i, chapter._id)}>
                                {chapter.name}
                            </div>

                        );
                    })
                }
            </div>
        </div>
    );
}
export default SectionCard;
