import React, {Component, useEffect, useState} from "react";
import SectionCard from "../components/SectionCard";
import "./Section.css";
import {useStateValue} from "../contextAPI/StateProvider";

function Section(){

    const [state, dispatch] = useStateValue();

    const sections = [
        [
            {
                name:"Physics",
                topic:[{name: "Electrostatics", id: ""}, {name: "Simple Harmonic Motion", id:""},
                    {name:""}]
            }
        ]


        ["Physics", "Chemistry", "Mathematics"],
        ["Database Management System", "Operating System", "Computer Networks", "Object Oriented Programming"],
        ["Aptitude Ability, English Language"]
    ];

    const [openClass, setOpenClass] = useState([
        false, false, false
    ]);

    const toggleSection = function (sectionId){

        setOpenClass(openClass.map((info, i) =>{
            if(i == sectionId){
                info = !info
            }
            else{
                info = false;
            }
            return info;
        }));



    };

    return(
        <div className={"section"}>
            <h1>{state.testTitle}</h1>
            <div className={"subsection"}>
                {sections[state.testId].map((sectionName, i) => {
                    return(
                        <div>
                            <SectionCard sectionId={i}
                                         toggleSection = {toggleSection}
                                         openClass = {openClass}
                                         sectionName = {sectionName}
                            />
                        </div>
                    )
                })}
            </div>
        </div>
    );
}
export default Section;
