import React, {useState} from "react";
import "./Subject.css";
import {useHistory} from "react-router-dom";
import {useStateValue} from "../contextAPI/StateProvider";

function SectionCard({sectionId, toggleSection, openClass, section}){
    const [state, dispatch] = useStateValue();

    const topic = []


    const history = useHistory();


    const createConfirm = (topic_id) =>{

        fetch(`http://localhost:5000/topics/${topic_id}/questions`)
            .then((res)=>res.json())
            .then((data)=>{
                console.log(data);
                dispatch({
                    type: 'UPDATE_TOPIC_QUESTIONS',
                    item: data
                });
                setTimeout(()=>{console.log(state.topicQuestions);}, 2000);

            });

        // eslint-disable-next-line no-restricted-globals
        let flag = confirm("Are you sure you want to do Quiz?");
        if(flag){

            history.push('/quiz');
        }
    }

    return(
        <div className={"subject "+ (openClass[sectionId]? "open": "")}
            key={sectionId}
             onClick={()=>toggleSection(sectionId)}
        >
            <div className={"subject__heading"}>
                {section.name}
            </div>
            <div className={"subject__topics"}>
                {

                    section.topics.map((topic) =>{
                        return(
                            <div onClick={()=>createConfirm(topic.id)}>
                                {topic.name}
                            </div>

                        );
                    })
                }
            </div>
        </div>
    );
}
export default SectionCard;
