import React from "react";

export const initialState = {
    test:[]
};

function reducer(state, action){

    switch (action.type){
        case 'UPDATE_QUIZ_INFO':
            //Logic for adding item to basket
            return {
                ...state,
                quiz: action.item
            }

        default:
            return state;
    }
}

export default reducer;
