import React, {useEffect, useState} from "react";
import "./TimerHeader.css";
import Button from "@material-ui/core/Button";
import {useHistory} from "react-router-dom";
import {useStateValue} from "./StateProvider";

function TimerHeader(){

    const [{quiz}, dispatch] = useStateValue();

    const [hour, setHour] = useState(["00"]);
    const [minute, setMinute] = useState(["15"]);
    const [second, setSecond] = useState(["14"]);

    const history = useHistory();

    const resultCalculation = function (){
        let answers = [], result = [];

        quiz.map((info, i) =>{
            answers.push(info.answer.index);
        });

        let elements = document.querySelectorAll(".questionCard__question__options");
        let totalCorrectAnswer =0;

        for(let i = 0; i<elements.length; i++){
            let userAnswer = (elements[i]
                .querySelector('input[name=question'+i+']:checked')||{}).value;
            if(userAnswer == answers[i]){
                totalCorrectAnswer++;

                quiz[i].result.class = "resultSuccess";

                dispatch({
                    type: 'UPDATE_QUIZ_INFO',
                    item: quiz
                });
            }
            else{
                quiz[i].result.class = "resultFailure";
                dispatch({
                    type: 'UPDATE_QUIZ_INFO',
                    item: quiz
                });
            }
        }

        setTimeout(()=>{
            alert("You scored "+ totalCorrectAnswer + " out of "+answers.length);
        }, 1000)

    };

    useEffect(() => {
        (function (){
            setTimeout(function (){
                if(second == "00"){
                    if(minute =="00"){
                        resultCalculation();
                    }
                    else {
                        var min = parseInt(minute) - 1;
                        if (min < 10)
                            min = "0" + min.toString();
                        setMinute(min);
                        setSecond("59");
                    }
                }
                else if(second != "00"){
                    var sec = parseInt(second)-1;
                    if(sec < 10)
                        sec = "0"+sec.toString()
                    setSecond(sec);
                }
            }, 1000);
        })();

        return ()=>{};

    }, [second]);

    return(
        <div className={"timerHeader"}>
            <div className={"timerHeader__title"}>
                <p>Electrostatics</p>
            </div>
            <div className={"timerHeader__clock"}>
                <p>Time Left </p>

                <div className={"timerHeader__clock__container"}>
                    <div className={"timerHeader__clock__time"}>
                        {hour}
                    </div>
                    <p>:</p>
                    <div className={"timerHeader__clock__time"}>
                        {minute}
                    </div>
                    <p>:</p>
                    <div className={"timerHeader__clock__time"}>
                        {second}
                    </div>
                </div>
            </div>
            <div className={"timerHeader__button"}>
                <Button variant="contained" color="primary" onClick={resultCalculation}>
                    End Test
                </Button>
            </div>
        </div>
    );
}

export default TimerHeader;
