import React, {useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";
import "./SidePanel.css";
import {setActiveQuestion, updateVisitedQuestions} from "../contextAPI/actions";

function SidePanel(){
    const [state, dispatch] = useStateValue();

    const [flag, setFlag] = useState(false);

    const innerWidth = window.innerWidth;

    const toggleStyles = {

        openSide:{
            sidePanel:{
                width:"70vw",
            },
            sidePanel__user_pTag:{
                display: "block"
            },
            sidePanel__buttonExplained:{
                display: "flex"
            },
            sidePanel__questionNavigation:{
                display: "flex"
            },
            sidePanel__hr:{
                display: "block"
            }
        },
        closeSide:{
            sidePanel:{
                width:"10vw",
                zIndex: 0
            },
            sidePanel__user_pTag:{
                display: "none"
            },
            sidePanel__buttonExplained:{
                display: "none"
            },
            sidePanel__questionNavigation:{
                display: "none"
            },
            sidePanel__hr:{
                display: "none"
            }
        }
    }

    const updateActive = (i) => {
        setActiveQuestion(i+1, dispatch);

        updateVisitedQuestions(i, dispatch);
        console.log(state.activeQuestion.length)
    }

    const toggleFlag = () =>{
        let leftWidth, rightWidth;
        if(!flag){
            leftWidth = '30vw';
            rightWidth = '70vw';
        }
        else{
            leftWidth = '90vw';
            rightWidth = '10vw';
        }
        let all = document.getElementsByClassName('quiz__content__leftSide');
        for(let i=0; i<all.length; i++){
            all[i].style.width = leftWidth;
        }
        all = document.getElementsByClassName('quiz__content__rightSide');
        for(let i=0; i<all.length; i++){
            all[i].style.width = rightWidth;
        }

        setFlag(!flag);
    }

    return(
        <div className={"sidePanel"} style={
            flag ? (toggleStyles.openSide.sidePanel):(toggleStyles.closeSide.sidePanel)
        }>
            <div className={"sidePanel__user"}>

                <p style={
                    innerWidth > 720 ? ({display: "block"}): (
                        flag ? (toggleStyles.openSide.sidePanel__user_pTag): (toggleStyles.closeSide.sidePanel__user_pTag)
                    )
                }>
                    {"Rohit"}
                </p>
                {
                    innerWidth < 720 ? (
                        <div className={"toggleBar btn"} onClick={toggleFlag}>
                            &#9776;
                        </div>
                    ):(<div></div>)
                }
            </div>

            <hr style={
                innerWidth > 720 ? ({display: "block"}): (
                    flag ? (toggleStyles.openSide.sidePanel__hr):(toggleStyles.closeSide.sidePanel__hr)
                )
            }/>

            <div className={"sidePanel__buttonExplained"} style={
                innerWidth > 720 ? ({display: "block"}): (
                    flag ? (toggleStyles.openSide.sidePanel__buttonExplained): (toggleStyles.closeSide.sidePanel__buttonExplained)
                )
            }>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button  className={"btnCommonAttr answered"} disabled={true}>2</button>
                    <p>Answered</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button  className={"btnCommonAttr notVisited"} disabled={true}>0</button>
                    <p>Not Visited</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button className={"notAnswered btnCommonAttr"} disabled={true}>1</button>
                    <p>Not Answered</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button className={"activeInfo btnCommonAttr"} disabled={true}>5</button>
                    <p>Active</p>
                </div>
            </div>

            <hr style={
                innerWidth > 720 ? ({display: "block"}): (
                    flag ? (toggleStyles.openSide.sidePanel__hr):(toggleStyles.closeSide.sidePanel__hr)
                )
            }/>

            <div className={"sidePanel__questionNavigation"} style={
                innerWidth > 720 ? ({display: "block"}): (
                    flag ? (toggleStyles.openSide.sidePanel__questionNavigation): (toggleStyles.closeSide.sidePanel__questionNavigation)
                )
            }>
                <div className={"sidePanel__questionNavigation__title"}>
                    <strong>Section:</strong>
                    <p style={{paddingLeft: "10px"}}>{state.topicDetails.name}</p>
                </div>
                <div style={{height:"50vh"}}>
                    <div className={"sidePanel__questionNavigation__buttons"}>
                        {
                            state.topicDetails.questions.map((info, index)=>{
                                if(state.activeQuestion == index+1) {
                                    return (
                                        <button id={index + 1} className={"active btnCommonAttr"} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    );
                                }
                                else if(state.answeredQuestion[index]){
                                    return (
                                        <button id={index + 1} className={"answered btnCommonAttr"} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    )
                                }
                                else if(state.visitedQuestion[index]){
                                    return (
                                        <button id={index + 1} className={"notAnswered btnCommonAttr"} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    )
                                }
                                else {
                                    return (
                                        <button id={index + 1} className={"notVisited btnCommonAttr"} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    );
                                }
                            })
                        }
                    </div>
                </div>

            </div>

        </div>
    );
}

export default SidePanel;
