import React, {useEffect, useState} from "react";
import "./Quiz.css";
import TimerHeader from "../components/TimerHeader";
import QuestionCard from "../components/QuestionCard";
import {useStateValue} from "../contextAPI/StateProvider";

function Quiz(){
    const [state, dispatch] = useStateValue();
    // const [questions, setQuestions] = useState([]);
    // const [status, setStatus] = useState([]);

    useEffect(()=>{

        fetch(`http://localhost:5000/topics/${state.topicId}`)
            .then(res => res.json())
            .then(data=>{

                let numberOfQuestions = data.questions.length;
                let result = [];
                for(let i=0; i<numberOfQuestions; i++){
                    result.push({
                        class: ""
                    });
                }
                dispatch({
                    type: 'UPDATE_TOPIC_DETAILS',
                    item: data
                });
                dispatch({
                    type: 'UPDATE_RESULT_CLASS',
                    item: result
                });
                console.log(state.topicDetails);
                console.log(state.result);
            });

        return ()=>{};
    }, []);

    return(
        <div className={"quiz"}>
            {/*<TimerHeader questions={questions}*/}
            {/*            resultStatus={status}*/}
            {/*/>*/}
            <TimerHeader/>
            <div className={"quiz__questionSet"}>
                {
                    state.topicDetails.questions.map((info, i)=>{
                    return(
                        <div className={"quiz__questionSet__question"}>
                            <QuestionCard question={info.text}
                                          answer={info.answer}
                                          options={info.options}
                                          index={i}
                            />
                        </div>

                        )

                })}
            </div>

        </div>
    );
}

export default Quiz;
