import React, {useEffect, useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";

function QuestionPanel(){
    const [state, dispatch] = useStateValue();


    const [minute, setMinute] = useState([parseInt(state.questionTimer)/60]);
    const [second, setSecond] = useState([parseInt(state.questionTimer)%60]);

    useEffect(() => {
        (function (){
            if(minute == "00")
                setMinute("00")
            setTimeout(function (){
                if(second == "60"){
                    let min = parseInt(minute)+1;
                    if (min < 10)
                        min = "0" + min.toString();
                    setMinute(min);
                    setSecond(0);
                }
                else if(second != "60"){
                    let sec = parseInt(second)+1;
                    if(sec < 10)
                        sec = "0"+sec.toString()
                    setSecond(sec);
                }
            }, 1000);
        })();

        return ()=>{};
    }, [second]);

    return(
        <div className={"questionPanel"}>
            <div className={"questionPanel__header"}>
                <strong>Question No. {state.activeQuestion}</strong>
                <div className={"questionPanel__header__marksDescription"}>
                    <p>Marks</p>
                    <div className={"questionPanel__header__marksDescription__digit"}>
                        <a>+4</a>
                        <a>-1</a>
                    </div>
                </div>
                <div className={"questionPanel__header__clock"}>
                    <p>Time</p>
                    <div className={"questionPanel__header__clock__time"}>
                        <p>{minute}</p>
                        <p>{second}</p>
                    </div>
                </div>

            </div>
        </div>
    );
}

export default QuestionPanel;
