import React, {useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";
import "./SidePanel.css";
import {setActiveQuestion, updateVisitedQuestions} from "../contextAPI/actions";

function SidePanel(){
    const [state, dispatch] = useStateValue();

    const [flag, setFlag] = useState(false);

    const innerWidth = window.innerWidth;

    const styles = {
        answered: {
            background: "green",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            marginLeft: "1vw"
        },
        notVisited: {
            background: "white",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            outline:"none",
            marginLeft: "1vw"
        },
        notAnswered:{
            background: "red",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            marginLeft: "1vw"
        },
        activeInfo:{
            background: "orange",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            borderRadius:"40%",
            marginLeft: "0vw"
        },
        active:{
            background: "orange",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            borderRadius:"40%",
            borderColor: "orange",
            outline:"none"
        }
    }

    const toggleStyles = {

        openSide:{
            sidePanel:{
                width:"70vw",
                zIndex: 1
            },
            sidePanel__user_pTag:{
                display: "block"
            },
            sidePanel__buttonExplained:{
                display: "block"
            },
            sidePanel__questionNavigation:{
                display: "block"
            },
            sidePanel__hr:{
                display: "block"
            }
        },
        closeSide:{
            sidePanel:{
                width:"10vw",
                zIndex: 0
            },
            sidePanel__user_pTag:{
                display: "none"
            },
            sidePanel__buttonExplained:{
                display: "none"
            },
            sidePanel__questionNavigation:{
                display: "none"
            },
            sidePanel__hr:{
                display: "none"
            }
        }
    }

    const updateActive = (i) => {
        setActiveQuestion(i+1, dispatch);

        updateVisitedQuestions(i, dispatch);
        console.log(state.activeQuestion.length)
    }

    return(
        <div className={"sidePanel"} style={
            flag ? (toggleStyles.openSide.sidePanel):(toggleStyles.closeSide.sidePanel)
        }>
            <div className={"sidePanel__user"}>
                <p style={
                    flag ? (toggleStyles.openSide.sidePanel__user_pTag): (toggleStyles.closeSide.sidePanel__user_pTag)
                }>
                    {"Rohit"}
                </p>
                {
                    innerWidth < 720 ? (
                        <div className={"toggleBar btn"} onClick={() => setFlag(!flag)}>
                            &#9776;
                        </div>
                    ):(<div></div>)
                }
            </div>

            <hr style={flag ? (toggleStyles.openSide.sidePanel__hr):(toggleStyles.closeSide.sidePanel__hr)}/>

            <div className={"sidePanel__buttonExplained"} style={
                flag ? (toggleStyles.openSide.sidePanel__buttonExplained): (toggleStyles.closeSide.sidePanel__buttonExplained)
            }>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.answered} disabled={true}>2</button>
                    <p>Answered</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.notVisited} disabled={true}>0</button>
                    <p>Not Visited</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.notAnswered} disabled={true}>1</button>
                    <p>Not Answered</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.activeInfo} disabled={true}>5</button>
                    <p>Active</p>
                </div>
            </div>

            <hr style={flag ? (toggleStyles.openSide.sidePanel__hr):(toggleStyles.closeSide.sidePanel__hr)}/>

            <div className={"sidePanel__questionNavigation"} style={
                flag ? (toggleStyles.openSide.sidePanel__questionNavigation): (toggleStyles.closeSide.sidePanel__questionNavigation)
            }>
                <div className={"sidePanel__questionNavigation__title"}>
                    <strong>Section:</strong>
                    <p style={{paddingLeft: "10px"}}>{state.topicDetails.name}</p>
                </div>
                <div style={{height:"40vh"}}>
                    <div className={"sidePanel__questionNavigation__buttons"}>
                        {
                            state.topicDetails.questions.map((info, index)=>{
                                if(state.activeQuestion == index+1) {
                                    return (
                                        <button id={index + 1} style={styles.active} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    );
                                }
                                else if(state.answeredQuestion[index]){
                                    return (
                                        <button id={index + 1} style={styles.answered} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    )
                                }
                                else if(state.visitedQuestion[index]){
                                    return (
                                        <button id={index + 1} style={styles.notAnswered} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    )
                                }
                                else {
                                    return (
                                        <button id={index + 1} style={styles.notVisited} onClick={()=>updateActive(index)}>
                                            {index + 1}
                                        </button>
                                    );
                                }
                            })
                        }
                    </div>
                </div>

            </div>

        </div>
    );
}

export default SidePanel;
