import React from "react";
import {useStateValue} from "../contextAPI/StateProvider";
import "./SidePanel.css";

function SidePanel(){
    const [state, dispatch] = useStateValue();
    const styles = {
        answered: {
            background: "green",
            marginRight: "0.5rem",
            width: "1.5rem",
            height: "1.5rem",
            color: "black"
        },
        notVisited: {
            background: "white",
            marginRight: "0.5rem",
            width: "1.5rem",
            height: "1.5rem",
            color: "black"
        },
        notAnswered:{
            background: "red",
            marginRight: "0.5rem",
            width: "1.5rem",
            height: "1.5rem",
            color: "black"
        },
        active:{
            background: "white",
            marginRight: "0.5rem",
            border: "none",
            width: "1.5rem",
            height: "1.5rem",
            color: "black",
            borderRadius:"40%"
        }

    }

    return(
        <div className={"sidePanel"}>
            <div className={"sidePanel__user"}>
                <p>{state.userDetails.name}</p>
            </div>

            <hr/>

            <div className={"sidePanel__buttonExplained"}>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.answered} disabled={true}>2</button>
                    <p>Answered</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.notVisited} disabled={true}>0</button>
                    <p>Not Visited</p>
                </div>
                <div className={"sidePanel__buttonExplained__box"}>
                    <button style={styles.notAnswered} disabled={true}>1</button>
                    <p>Not Answered</p>
                </div>
            </div>

            <hr/>

            <div className={"sidePanel__questionNavigation"}>
                <div className={"sidePanel__questionNavigation__title"}>
                    <strong>Section:</strong>
                    <p style={{paddingLeft: "10px"}}>{state.topicDetails.name}</p>
                </div>
                <div className={"sidePanel__questionNavigation__buttons"}>
                    {
                        state.topicDetails.questions.map((info, index)=>{
                            if(state.activeQuestion == index+1) {
                                return (
                                    <button id={index + 1} style={styles.active}>
                                        {index + 1}
                                    </button>
                                );
                            }
                        })
                    }
                </div>
            </div>

            <hr/>

            <div className={"sidePanel__instruction"}>
                <button>Instructions</button>
            </div>
            <hr/>

        </div>
    );
}

export default SidePanel;
