import React, {useEffect, useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";

function QuestionPanel(){
    const [state, dispatch] = useStateValue();
    const [second, setSecond] = useState();
    const [minute, setMinute] = useState();

    useEffect(() => {
        let time = parseInt(state.questionTimer[state.activeQuestion-1]);
        let minute = time/60;
        let second = time%60;

        if(second<10)
            setSecond('0' + second.toString());
        else
            setSecond(second.toString());

        if(minute<10)
            setMinute('0' + minute.toString());
        else
            setMinute(minute.toString());

        return ()=>{}
    }, []);

    return(
        <div className={"questionPanel"}>
            <div className={"questionPanel__header"}>
                <strong>Question No. {state.activeQuestion}</strong>
                <div className={"questionPanel__header__marksDescription"}>
                    <p>Marks</p>
                    <div className={"questionPanel__header__marksDescription__digit"}>
                        <a>+4</a>
                        <a>-1</a>
                    </div>
                </div>
                <div className={"questionPanel__header__clock"}>
                    <p>Time</p>
                    <div className={"questionPanel__header__clock__time"}>
                        <p>{minute}</p>
                        <p>{second}</p>
                    </div>
                </div>

            </div>
        </div>
    );
}

export default QuestionPanel;
