import React, {useEffect, useState} from "react";
import "./Quiz.css";
import TimerHeader from "../components/TimerHeader";
import QuestionCard from "../components/QuestionCard";
import {useStateValue} from "../contextAPI/StateProvider";

function Quiz(){
    const [state, dispatch] = useStateValue();
    const [questions, setQuestions] = useState([]);
    const [status, setStatus] = useState([]);

    useEffect(()=>{

        fetch(`http://localhost:5000/topics/${state.topicId}/questions`)
            .then(res => res.json())
            .then(data=>{
                console.log(data);

                setQuestions(data);
                let numberOfQuestions = questions.length;
                let result = [];
                for(let i=0; i<numberOfQuestions; i++){
                    result.push({
                        status: false,
                        class: ""
                    });
                }
                dispatch({
                    type: 'UPDATE_RESULT',
                    item: result
                })
                console.log(state.result);
            });

        return ()=>{};
    }, []);

    return(
        <div className={"quiz"}>
            <TimerHeader questions={questions}
                        resultStatus={status}
            />
            <div className={"quiz__questionSet"}>
                {
                    questions.map((info, i)=>{
                    return(
                        <div className={"quiz__questionSet__question"}>
                            <QuestionCard question={info.text}
                                          answer={info.answer}
                                          options={info.options}
                                          index={i}
                                          resultStatus = {status}
                            />
                        </div>

                        )

                })}
            </div>

        </div>
    );
}

export default Quiz;
