import {useStateValue} from "./StateProvider";

const [state, dispatch] = useStateValue();

export const updateTestId = (id) => {
    dispatch({
        type: 'UPDATE_TEST_ID',
        item: id
    })
}

export const updateTestTitle = (title) => {
    dispatch({
        type: 'UPDATE_TEST_TITLE',
        item: title
    })
}



export const updateTopicId = (id) => {
    dispatch({
        type: 'UPDATE_TOPIC_ID',
        item: id
    })
}



// export const
