import React from "react";

export const initialState = {
    testId: "5f56490e44baf43fac3c8ede",
    testTitle: "Jee Advance",
    testStreamsInfo: [
        {
            "topic": [
                {
                    "questions": [
                        {
                            "options": [
                                "Electrostatic energy",
                                "Electric Field",
                                "Electric Potential",
                                "Electric force"
                            ],
                            "_id": "5f56490e44baf43fac3c8ee1",
                            "text": "Superposition principle can not be applied to these quantities in electrostatics.",
                            "answer": {
                                "_id": "5f56490e44baf43fac3c8ee2",
                                "index": 1,
                                "value": "Electrostatic energy"
                            }
                        },
                        {
                            "options": [
                                "Electrostatic energy is conserved",
                                "Charge is conserved",
                                "Both the charge and electrostatic energy is conserved",
                                "Neither of these is conserved"
                            ],
                            "_id": "5f56490e44baf43fac3c8ee3",
                            "text": "Two conducting charged spheres X and Y having unequal charges are connected by the wire. Which of the following is true?",
                            "answer": {
                                "_id": "5f56490e44baf43fac3c8ee4",
                                "index": 2,
                                "value": "Charge is conserved"
                            }
                        }
                    ],
                    "_id": "5f56490e44baf43fac3c8ee0",
                    "name": "Electrostatics"
                }
            ],
            "_id": "5f56490e44baf43fac3c8edf",
            "name": "physics",
            "open": false
        }
    ],
    testStreamId: "5f56490e44baf43fac3c8edf",

    topicDetails: {
        "questions": [
            {
                "options": [
                    "Electrostatic energy",
                    "Electric Field",
                    "Electric Potential",
                    "Electric force"
                ],
                "_id": "5f56490e44baf43fac3c8ee1",
                "text": "Superposition principle can not be applied to these quantities in electrostatics.",
                "answer": {
                    "_id": "5f56490e44baf43fac3c8ee2",
                    "index": 1,
                    "value": "Electrostatic energy"
                }
            },
            {
                "options": [
                    "Electrostatic energy is conserved",
                    "Charge is conserved",
                    "Both the charge and electrostatic energy is conserved",
                    "Neither of these is conserved"
                ],
                "_id": "5f56490e44baf43fac3c8ee3",
                "text": "Two conducting charged spheres X and Y having unequal charges are connected by the wire. Which of the following is true?",
                "answer": {
                    "_id": "5f56490e44baf43fac3c8ee4",
                    "index": 2,
                    "value": "Charge is conserved"
                }
            }
        ],
        "_id": "5f56490e44baf43fac3c8ee0",
        "name": "Electrostatics"
    },
    result:[{
        status: false,
        class: ""
    },{
        status: false,
        class: ""
    }]
    ,
    user:"rohit",
    userRecord:[{
        testTitle:"Advanced Physics",
        totalMarks: 10,
        scoredMarks: 15
    }],

};

function reducer(state, action){

    switch (action.type){
        case 'UPDATE_QUIZ_INFO':
            //Logic for adding item to basket
            return {
                ...state,
                quiz: action.item
            }
        case 'UPDATE_TEST_ID':
            //Logic for updating test id
            return {
                ...state,
                testId: action.item
            }
        case 'UPDATE_TEST_TITLE':
            //Logic for updating test title
            return {
                ...state,
                testTitle: action.item
            }
        case 'UPDATE_TEST_STREAMS_INFO':
            //Logic for updating test streams
            return {
                ...state,
                testStreamsInfo: action.item
            }
        case 'UPDATE_TEST_STREAM_ID':
            //Logic for updating test title
            return {
                ...state,
                testStreamId: action.item
            }
        case 'UPDATE_TOPIC_DETAILS':
            //Logic for updating test title
            return {
                ...state,
                topicDetails: action.item
            }
        case 'UPDATE_RESULT':
            //Logic for updating test title
            return {
                ...state,
                result: action.item
            }
        case 'UPDATE_USER_ID':
            //Logic for updating test title
            return {
                ...state,
                userId: action.item
            }
        case 'UPDATE_USER_RECORD':
            //Logic for updating test title
            return {
                ...state,
                userRecord:[...state.userRecord, action.item]
            }
        default:
            return state;
    }
}

export default reducer;
