import React, {useEffect, useState} from "react";
import {useStateValue} from "../contextAPI/StateProvider";
import "./QuestionPanel.css";

function QuestionPanel(){
    const [state, dispatch] = useStateValue();


    const [minute, setMinute] = useState([parseInt(parseInt(state.questionTimer[state.activeQuestion-1])/60).toString()]);
    const [second, setSecond] = useState([parseInt(parseInt(state.questionTimer[state.activeQuestion-1])%60).toString()]);

    const [question, setQuestion] = useState(state.topicDetails.questions[state.activeQuestion-1]);

    const [flag, setFlag] = useState(false);

    const updateState = () => {

        setFlag(false);

        dispatch({
            type: 'SET_VISITED_QUESTIONS',
            item: parseInt(state.activeQuestion) - 1
        })

        let element = document.getElementById(state.activeQuestion);

        let userAnswer = (element
            .querySelector('input[name=question'+state.activeQuestion+']:checked')||{}).value;
        console.log(userAnswer);

        dispatch({
            type: 'UPDATE_YOUR_ANSWER',
            item: userAnswer
        });

        if(userAnswer != undefined){
            dispatch({
                type:'SET_ANSWERED_QUESTION',
                item: true
            })
        }else{
            dispatch({
                type:'SET_ANSWERED_QUESTION',
                item: false
            })
        }

        let time = parseInt(minute)*60 + parseInt(second);
        dispatch({
            type:'UPDATE_QUESTION_TIMER',
            item: time
        })

        let k = (parseInt(state.activeQuestion) % state.topicDetails.questions.length)+1;
        dispatch({
            type: 'SET_ACTIVE_QUESTION',
            item: k
        });
    }

    // useEffect(() => {
    //     if(state.yourAnswer[state.activeQuestion-1] == 0){
    //         for(let i = 0; i<4; i++){
    //             document.getElementById(state.activeQuestion+"_"+i+1).checked = false;
    //         }
    //     }
    //     else{
    //         console.log(state.activeQuestion+"_"+state.yourAnswer[state.activeQuestion-1]);
    //
    //         document.getElementById(state.activeQuestion+"_"+state.yourAnswer[state.activeQuestion-1])
    //             .checked = true;
    //
    //     }
    //
    // }, [state.activeQuestion])

    useEffect(() =>{
        setTimeout(function (){
            setFlag(true);

            setQuestion(state.topicDetails.questions[state.activeQuestion-1]);
            let min = parseInt(parseInt(state.questionTimer[state.activeQuestion-1])/60).toString();
            if(min<10)
                min = "0"+min;
            console.log(min);

            setMinute(min);

            let sec = parseInt(parseInt(state.questionTimer[state.activeQuestion-1])%60).toString();
            if(sec<10)
                sec = "0"+sec;
            console.log(sec);

            setSecond(sec);

        }, 1200);
        return ()=>{}

    }, [state.activeQuestion])

    useEffect(() => {
        (function (){
            if(flag){
                setTimeout(function (){
                    if(minute == "0")
                        setMinute("00")

                    if(second == "59"){
                        let min = parseInt(minute)+1;
                        if (min < 10)
                            min = "0" + min.toString();
                        setMinute(min);
                        setSecond("00");
                    }
                    else if(second != "60"){
                        let sec = parseInt(second)+1;
                        if(sec < 10)
                            sec = "0"+sec.toString()
                        setSecond(sec);
                    }
                }, 1000);
            }
        })();

        return ()=>{};
    }, [second]);

    return(
        <div className={"questionPanel"}>
            <div className={"questionPanel__header"}>
                <strong className={"questionPanel__header__title"}>
                    Question No. {state.activeQuestion}
                </strong>
                <div className={"questionPanel__header__marking"}>
                    <p>Marks</p>
                    <div className={"questionPanel__header__marking__digit"}>
                        <a style={{background: "green"}}> +4 </a>
                        <a style={{background: "red"}}> -1 </a>
                    </div>
                </div>
                <div className={"questionPanel__header__clock"}>
                    <p>Time</p>
                    <div className={"questionPanel__header__clock__time"}>
                        <p>{minute}</p>
                        <a>:</a>
                        <p>{second}</p>
                    </div>
                </div>

            </div>

            <hr/>

            <div className={"questionPanel__question"}>
                <div className={"questionPanel__question__text"}>
                    <p>{question.text}</p>
                </div>
                <div className={"questionPanel__question__options"} id={state.activeQuestion}>
                    {
                        question.options.map((info, i)=>{
                            return(
                                <div className={"option"}>
                                    <input type={"radio"} name={"question"+state.activeQuestion}
                                           value={i+1} id={state.activeQuestion+"_"+(parseInt(i)+1)}/>
                                    <p>{info}</p>
                                </div>
                            );
                        })
                    }
                </div>
            </div>

            <hr/>
            <div className={"questionPanel__footer"}>
                <button className="questionPanel__footer__clear">Clear Response</button>
                <button className="questionPanel__footer__save" onClick={updateState}>Save & Next</button>
            </div>
            <hr/>
        </div>
    );
}

export default QuestionPanel;
