import React from "react";
import "./QuestionCard.css";
import {useStateValue} from "../contextAPI/StateProvider";

function QuestionCard({question, options, answer, index}){

    const [state, _] = useStateValue();

    return(
        <div className={"questionCard"}>
            <div className={"questionCard__index"}>
                <p>Question No. {index+1}</p>
            </div>
            <div className={"questionCard__question"}>
                <div className={"questionCard__question__text"}>
                    <p>{question}</p>
                </div>
                <div className={"questionCard__question__options"}>
                    {
                        options.map((info, i)=>{
                            return(
                                <div className={"option"}>
                                    <input type={"radio"} name={"question"+index} value={i+1}/>
                                    <p>{info}</p>
                                </div>
                            );
                        })
                    }
                </div>
            </div>
            <div className={"questionCard__result"+index+" questionCard__result__container "+state.result[index].class}>
                <p>Correct Answer is: {answer.value}</p>
            </div>


        </div>
    );
}

export default QuestionCard;
